mapper.select:
select   
from admin


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and avatar = #{avatar}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from admin


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and avatar = #{avatar}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}
